import {promiseSignal} from './lib/promise_helpers'
import {asAsyncIterator} from './lib/get_iterator'

const True = true

function timeoutTrigger(state, period) {
  clearTimeout(state.timerHandle)
  const timeout = promiseSignal()
  state.timerHandle = setTimeout(() => timeout.res(), period)
  state.promise = timeout.promise.then(() => ({timed: true}))
}

function returnLastValue(state) {
  if (state.buffer.length > 0) {
    state.donedone = true
    const emittedValue = state.buffer
    state.buffer = []
    return {value: emittedValue, done: false}
  }
  return {done: true}
}

function pushValue(state, value) {
  state.buffer.push(value)
  state.nextValue = undefined
}

function packageNextEmit(state, period) {
  const emittedValue = state.buffer
  state.buffer = []
  timeoutTrigger(state, period)
  if (emittedValue.length > 0)
    return {value: emittedValue, done: false}
}

export async function bufferBy(source, trigger, maxWaitTime) {
  const state = {
    buffer: [],
    nextValue: undefined,
    donedone: false,
    timeout: undefined
  }

  source = await asAsyncIterator(source)

  /* eslint complexity: ['error', 9] */
  return asAsyncIterator({
    [Symbol.asyncIterator]() {
      return {
        async next() {
          if (state.donedone)
            return {done: true}

          timeoutTrigger(state, maxWaitTime)

          while (True) {
            state.nextValue = state.nextValue || source.next()
            const {value, done, timed} = await Promise.race([state.nextValue, state.promise])
            if (done)
              return returnLastValue(state)

            let emittedValue = undefined
            if (timed || trigger(value, [...state.buffer, value]))
              if (emittedValue = packageNextEmit(state, maxWaitTime))
                return emittedValue

            if (!timed)
              pushValue(state, value)
          }
        },
        async return() {
          source.return()
          return {done: true}
        }
      }
    }
  })
}

import {asAsyncIterator} from './lib/get_iterator'

const True = true
const delay = period => new Promise(res => setTimeout(res, period))

export function bufferGroupBy(source, fn, trigger, maxWaitTime) {
  const state = {
    buffers: {},
    nextValue: undefined,
  }

  function emit(key) {
    const value = state.buffers[key].data
    delete state.buffers[key]
    return {value}
  }

  /* eslint complexity: ['error', 29] */
  return asAsyncIterator({
    [Symbol.asyncIterator]() {
      return {
        async next() {
          while (True) {
            state.nextValue = state.nextValue || source.next()
            const timers = Object.values(state.buffers).map(t => t.timer)
            const {value, done, timed} = await Promise.race([state.nextValue, ...timers])

            if (done) {
              const keys = Object.keys(state.buffers)
              if (keys.length === 0)
                return {done: true}

              return emit(keys[0])
            }

            if (timed)
              return emit(timed)

            const groupKey = fn(value)
            if (!state.buffers[groupKey])
              state.buffers[groupKey] = {
                timer: delay(maxWaitTime).then(() => ({timed: groupKey})),
                data: []
              }
            state.buffers[groupKey].data.push(value)
            state.nextValue = undefined

            if (trigger(value, state.buffers[groupKey].data))
              return emit(groupKey)
          }
        },
        async return() {
          state.buffers = {}
          state.nextValue = undefined
          source.return()
          return {done: true}
        }
      }
    }
  })
}

