import {pump} from './pump'

/**
 * Returns an iterator, that emits as per the `dataEvent` of the `eventSource`
 * @param  {EventEmitter} eventSource   An object that supports the 'on' and 'removeListener' function
 * @param  {String}       dataEvent     The main dataEvent name to listen to
 * @param  {String}       closeEvent    When this event emits, the iteration is stopped
 * @return {iteration}                  An iterable source
 */
export function fromStream(eventSource, dataEvent = 'data', closeEvent = 'close') {
  return pump(async (target, hasStopped) => {
    await target.next()

    async function listener(...args) {
      if (eventSource.pause)
        eventSource.pause()
      await target.next(...args)
      if (eventSource.resume)
        eventSource.resume()
    }

    async function closeListener() {
      target.return()
    }

    eventSource.on(dataEvent, listener)
    eventSource.on(closeEvent, closeListener)
    hasStopped.then(() => {
      eventSource.removeListener(dataEvent, listener)
      eventSource.removeListener(closeEvent, closeListener)
    })
  })
}

import {pump} from './pump'

export function interval(period, cancel = new Promise(() => {})) {
  return pump(async target => {
    const result = await Promise.race([target.next(), cancel.then(() => ({done: true}))])
    if (result.done)
      return target.return()

    return new Promise(res => {
      cancel.then(() => {
        currentPromise = null
        clearInterval(intervalHandle)
        target.return()
        res()
      })
      let intervalHandle = undefined
      let currentPromise = undefined
      const intervalFunction = async () => {
        if (currentPromise)
          return

        const v = process.hrtime.bigint()
        currentPromise = await Promise.race([target.next(v), cancel.then(() => ({done: true}))])
        if (currentPromise.done) {
          currentPromise = null
          clearInterval(intervalHandle)
          await target.return()
          res()
        }
        currentPromise = null
      }

      intervalHandle = setInterval(intervalFunction, period)
    })
  })
}

export function promiseSignal(marker) {
  let res
  let rej
  const promise = new Promise((_res, _rej) => { res = _res; rej = _rej })
  return {promise, rej, res, marker}
}

